package com.qmuiteam.qmui.calendarimport java.text.SimpleDateFormatimport java.util.*/** * Created by dengfa on 2022/1/13 * 日期处理常用扩展 */fun Date.getFirstOfWeek(): Calendar {    val calendar: Calendar = Calendar.getInstance()    calendar.time = this    calendar.set(Calendar.DAY_OF_WEEK, 1)    return calendar}fun Date.plus(days: Int): Date {    return Date(this.time + days * 24 * 60 * 60 * 1000L)}fun Date.add(days: Int): Date {    val calendar: Calendar = Calendar.getInstance()    calendar.time = this    calendar.add(Calendar.DAY_OF_MONTH, days)    return calendar.time}fun Date.format(): String {    val formatter = SimpleDateFormat("yy-MM-dd HH:mm:ss")    return formatter.format(this)}fun Calendar.isSameDay(calendar: Calendar): Boolean {    return this[Calendar.YEAR] == calendar[Calendar.YEAR] && this[Calendar.MONTH] == calendar[Calendar.MONTH] && this[Calendar.DAY_OF_MONTH] == calendar[Calendar.DAY_OF_MONTH]}