package com.qmuiteam.qmui.recyclerimport android.content.Contextimport android.graphics.Color.blueimport android.util.AttributeSetimport android.view.LayoutInflaterimport android.widget.FrameLayoutimport android.widget.Toastimport androidx.core.content.ContextCompatimport com.qmuiteam.qmui.Rimport kotlinx.android.synthetic.main.plan_view_task.view.*/** * Created by dengfa on 2022/1/28 */class TaskView @JvmOverloads constructor(context: Context, attrs: AttributeSet? = null, defStyleAttr: Int = 0) : FrameLayout(context, attrs, defStyleAttr) {    init {        LayoutInflater.from(context).inflate(R.layout.plan_view_task, this, true)        setOnClickListener {            Toast.makeText(context, "${tvTaskName?.text}", Toast.LENGTH_SHORT).show()        }    }    fun setText(text: String) {        tvTaskName?.text = text    }}